import { Component, inject } from '@angular/core';
import { Title } from '@angular/platform-browser';
import {
  ActivatedRoute,
  NavigationEnd,
  Router,
  RouterOutlet,
} from '@angular/router';
import { NavRoutesService } from '@services/nav-routes.service';
import { routes } from './app.routes';

@Component({
  selector: 'app-root',
  imports: [RouterOutlet],
  templateUrl: './app.component.html',
  styleUrl: './app.component.scss',
})
export class AppComponent {
  navRoutesService = inject(NavRoutesService);

  constructor(
    private router: Router,
    private titleService: Title,
    private activatedRoute: ActivatedRoute,
  ) {
    // Here we are subscribing to the router events to set the title of the page
    // based on the data property of the route. The title is set to "Parsley | Route Title"
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        this.titleService.setTitle(
          `Parsley | ${
            this.activatedRoute.snapshot.firstChild?.data['title'] ||
            'Route Title'
          }`,
        );
      }
    });
    // Generate the routes for the side nav
    // This is done in the constructor of the NavRoutesService
    // IN THIS PLACE IT TIS BEING INITIALIZED AGAIN
    this.navRoutesService.generateRoutesForNav(routes);
  }
}

import { MatCardModule } from '@angular/material/card';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { CommonModule } from '@angular/common';
import { LoginFormComponent } from './login-form/login-form.component';
import { SignupFormComponent } from './signup-form/signup-form.component';
import { AuthAction, AuthFormType } from '@models/auth-form.model';
import { SpinnerComponent } from '@shared/components/spinner/spinner.component';
import { GlobalStore } from 'app/state/global.state';
import { FormsModule, ReactiveFormsModule } from '@angular/forms';
import { Component, effect, inject, ChangeDetectionStrategy } from '@angular/core';

@Component({
  selector: 'app-auth-page',
  imports: [
    CommonModule,
    FormsModule,
    ReactiveFormsModule,
    MatCardModule,
    MatFormFieldModule,
    MatInputModule,
    MatButtonModule,
    SpinnerComponent,
    SignupFormComponent,
    LoginFormComponent,
  ],
  templateUrl: './auth-page.component.html',
  styleUrl: './auth-page.component.scss',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class AuthPageComponent {
  readonly globalStore = inject(GlobalStore);
  authFormTypes: typeof AuthFormType = AuthFormType;
  selectedFormType: 'login' | 'signup' = this.authFormTypes.LOGIN;

  constructor() {
    // When the user is manages to sirup successfully, we want to show the login form
    effect(() => {
      const isSignup = this.globalStore.isSignup();
      if (isSignup) {
        this.selectedFormType = this.authFormTypes.LOGIN;
      }
    });
  }

  /**
   *
   * @param type
   */
  onChoseFormType(type: 'login' | 'signup'): void {
    this.selectedFormType = type;
  }

  /**
   *
   * @param action
   */
  onAuthorize(action: AuthAction): void {
    const email = action.payload.email;
    const password = action.payload.password;
    if (email && password) {
      if (action.type === this.authFormTypes.LOGIN) {
        this.globalStore.loginUser(email, password);
      }
      if (
        action.type === this.authFormTypes.SIGNUP &&
        action.payload.password
      ) {
        this.globalStore.signUpNewUser(email, password);
      }
    }
  }
}

import { CommonModule } from '@angular/common';
import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'storybook-button',
  standalone: true,
  imports: [CommonModule],
  template: ` <button
  type="button"
  (click)="onClick.emit($event)"
  [ngClass]="classes"
  [ngStyle]="{ 'background-color': backgroundColor }"
>
  {{ label }}
</button>`,
  styleUrls: ['./button.css'],
})
export class ButtonComponent {
  /** Is this the principal call to action on the page? */
  @Input()
  primary = false;

  /** What background color to use */
  @Input()
  backgroundColor?: string;

  /** How large should the button be? */
  @Input()
  size: 'small' | 'medium' | 'large' = 'medium';

  /**
   * Button contents
   *
   * @required
   */
  @Input()
  label = 'Button';

  /** Optional click handler */
  @Output()
  onClick = new EventEmitter<Event>();

  public get classes(): string[] {
    const mode = this.primary ? 'storybook-button--primary' : 'storybook-button--secondary';

    return ['storybook-button', `storybook-button--${this.size}`, mode];
  }
}

