(req: HttpRequest<unknown>,
  next: HttpHandlerFn) => {
  const globalStore = inject(GlobalStore);
  const router = inject(Router);
  const authService = inject(AuthService);
  const token = authService.getBearerToken;

  if (!req.url.includes('login')) {
    if (req.url.startsWith(environment.apiUrl) && token) {
      req = addToken(req, token);
    } else {
      router.navigate(['/login']);
      authService.resetToken();
      globalStore.clearState();
      return EMPTY;
    }
  }

  return next(req).pipe(
    catchError((error: HttpErrorResponse) => {
      if (error.status === 401 && error.error.message === 'Access Denied. Invalid token.') {
        if (!isRefreshing) {
          isRefreshing = true;
          tokenSubject.next(null);
          return authService.refreshToken().pipe(
            switchMap((res: { token: string }) => {
              //Saving new token
              authService.setBearerToken = res.token;
              isRefreshing = false;
              tokenSubject.next(res.token);
              return next(addToken(req, res.token));
            }),
            catchError((error) => {
              isRefreshing = false;
              router.navigate(['/login']);
              authService.resetToken();
              globalStore.clearState();
              return throwError(() => error);
            })
          );
        } else {
          return tokenSubject.pipe(
            filter((token: string | null) => token != null),
            take(1),
            switchMap((token) => next(addToken(req, token))),
          );
        }
      }
      return throwError(() => error);
    })
  );
}

(
  req: HttpRequest<unknown>,
  next: HttpHandlerFn,
) => {
  const snackBar = inject(MatSnackBar);
  return next(req).pipe(
    tap({
      // Handle successful responses
      next: (event) => {
        if (
          event instanceof HttpResponse &&
          (event.status === 200 || event.status === 201)
        ) {
          snackBar.open('Operation successful', 'Close', {
            duration: 3000,
            panelClass: 'success-snack',
            horizontalPosition: 'right',
          });
        }
      },
    }),
    catchError((err: HttpErrorResponse) => {
      let errorMessage = '';

      if (err && err.error.error && err.error.error.message) {
        errorMessage = err.error.error.message + ' ' + err.error.error.details;
      } else {
        errorMessage = getErrorMessage(err);
      }

      snackBar.open(errorMessage, 'Close', {
        duration: 5000,
        panelClass: 'error-snack',
        horizontalPosition: 'right',
      });
      return throwError(() => err);
    }),
  );
}

signalStore(
  { providedIn: 'root' },
  withState<FoodState>(initialFoodState),
  withDevtools('foodStore'),
  withMethods((store) => ({
    stateInit: (): void => {
      patchState(store);
    },
    clearState: (): void => {
      patchState(store, initialFoodState);
    },
    setFoodShortList(foodShortList: FoodShortModel[]): void {
      patchState(store, { foodShortList });
    },
  })),
  withHooks({
    onInit: ({ stateInit }): void => {
      stateInit();
    },
  }),
)

(error: HttpErrorResponse): string => {
  switch (error.status) {
    case 400:
      return 'Invalid request';
    case 401:
      return 'Unauthorized';
    case 403:
      return 'Access denied';
    case 500:
      return 'Server error occurred';
    default:
      return 'An unexpected error occurred';
  }
}

signalStore(
  { providedIn: 'root' },
  withState<GlobalState>(initialGlobalState),
  withDevtools('globalStore'),
  withMethods(
    (store, authService = inject(AuthService), router = inject(Router)) => ({
      stateInit: (): void => {
        patchState(store, { appInit: true });
      },
      clearState: (): void => {
        patchState(store, initialGlobalState);
      },
      loginUser: (email: string, password: string): void => {
        patchState(store, {
          isLoading: true,
        });
        authService.loginUser(email, password).subscribe({
          next: () => {
            patchState(store, {
              isLoading: false,
              error: null,
              isUserAuthorized: true,
            });
            router.navigate(['/layout/dashboard']);
          },
          error: (err: HttpErrorResponse) => {
            patchState(store, { isLoading: false, error: err });
            console.error(err);
          },
        });
      },
      signUpNewUser: (email: string, password: string): void => {
        patchState(store, {
          isLoading: true,
          isSignup: false,
        });
        authService.signUpNewUser(email, password).subscribe({
          next: () => {
            patchState(store, {
              isLoading: false,
              error: null,
              isSignup: true,
            });
          },
          error: (err: HttpErrorResponse) => {
            patchState(store, { isLoading: false, error: err });
          },
        });
      },
      logout: (): void => {
        authService.logout().subscribe({
          next: () => {
            patchState(store, initialGlobalState);
            router.navigate(['/login']);
            authService.resetToken();
          },
          error: (err: HttpErrorResponse) => {
            console.log(err);
            patchState(store, initialGlobalState);
            router.navigate(['/login']);
            authService.resetToken();
          },
        });
      },
    }),
  ),
  withHooks({
    onInit: ({ stateInit }): void => {
      stateInit();
    },
    onDestroy: ({ clearState }): void => {
      clearState();
    },
  }),
)

signalState<FoodState>({
  foodShortList: [] as FoodShortModel[],
  error: null,
})

