})
export class DashboardComponent implements OnInit {
  navRoutesService = inject(NavRoutesService);
  routesForNav: RouteDataForDisplay[] = [];
  rippleColor = '#43a047';

  ngOnInit(): void {
    this.routesForNav = this.navRoutesService.routesForNav;
    this.removeDashboardRoute();
